mapper.select:
select user_id, question_id
from mid_user_follow_question
where user_id = #{userId}

mapper.update:
update user_recommend_question
set question_ids = #{questionIds}
where user_id = #{userId}

mapper.select:
select question_ids
from user_recommend_question
where user_id = #{userId}

select id
from user_recommend_question
where user_id = #{userId}
